import React from 'react';
import '../styles/InfoSection.css';

const InfoSection = () => {
  const handleReserve = () => {
    alert('กำลังนำคุณไปยังหน้าจองการชาร์จ...');
    // In production: window.location.href = '/reservation';
  };

  return (
    <section className="info-section">
      <div className="info-content animate-on-scroll">
        <h2 className="location-title">ศูนย์วัฒนธรรมเชียงใหม่</h2>
        <p className="info-text">
          ตั้งอยู่ที่ "Super EV Hub" สถานีชาร์จรถยนต์ไฟฟ้า<br/>
          ตรงข้ามศูนย์วัฒนธรรมเชียงใหม่ หน้ามหาวิทยาลัย<br/>
          เชียงใหม่ Ultra-Fast Charger 720 kW<br/>
          รวมสำหรับรถยนต์ไฟฟ้า ครบทุกรุ่นทุกยี่ห้อ<br/>
          ทำให้การเดินทางของคุณไม่สะดุดจึงดีต่อใจ
        </p>
        <button className="reserve-btn" onClick={handleReserve}>
          จองการชาร์จเลย
        </button>
      </div>
    </section>
  );
};

export default InfoSection;
