import React, { useEffect, useState } from 'react';
import '../styles/Hero.css';

const Hero = () => {
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  return (
    <section className="hero">
      <div className="logo-section">
        <div className="logo">⚡</div>
        <div className="brand-text">สถานีชาร์จรถไฟฟ้า</div>
      </div>

      <div className={`main-title ${isLoaded ? 'animate-in' : ''}`}>
        <h1>
          <span className="super">SUPER</span><br/>
          <span className="ev">EV <span className="lightning">⚡</span></span><br/>
          <span className="hub">HUB</span>
        </h1>
      </div>

      <div className={`tagline ${isLoaded ? 'animate-in' : ''}`}>
        <span>เร็วกว่า</span> <span className="highlight">แรงกว่า</span> <span>สะดวกกว่า</span>
      </div>

      {/* Chargers Visualization */}
      <div className="chargers-display">
        <ChargerUnit 
          position="left" 
          status="READY" 
          power="120 kW" 
          color="green"
        />

        <ChargerUnit 
          position="center" 
          status="SUPER" 
          power="350 kW" 
          color="yellow"
          isMain={true}
        />

        <ChargerUnit 
          position="right" 
          status="ULTRA FAST" 
          power="175 kW" 
          color="red"
        />
      </div>
    </section>
  );
};

const ChargerUnit = ({ position, status, power, color, isMain }) => {
  const [isCharging, setIsCharging] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setIsCharging(prev => !prev);
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className={`charger-unit ${position} ${isMain ? 'main' : ''}`}>
      <div className={`charger-screen ${color}`}>
        <span className="screen-text">{status}</span>
        <span className={`screen-text ${isCharging ? 'charging' : ''}`}>
          ⚡ {power}
        </span>
      </div>
      <div className="charging-port">
        <div className="cable"></div>
        {isCharging && (
          <>
            <div className="energy-flow"></div>
            <div className="energy-flow delay-1"></div>
            <div className="energy-flow delay-2"></div>
          </>
        )}
      </div>
    </div>
  );
};

export default Hero;
