import React, { useEffect, useState } from 'react';
import '../styles/Hero.css';
import logoImage from '../assets/superEVHubLogorez.webp';

const Hero = () => {
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  return (
    <section className="hero">

      {/* Logo and Brand Text */}
      <div className="logo-section">
        <img src={logoImage} alt="SUPER EV HUB Logo" className="logo" />
        <div className="brand-text">สถานีชาร์จรถไฟฟ้า SUPER EV HUB</div>
      </div>

      {/* Main Title */}
      <div className={`main-title ${isLoaded ? 'animate-in' : ''}`}>

        <h1>
          <span className="super">SUPER</span><br />
          <span className="ev">EV</span><br />
          <span className="hub">HUB</span>
        </h1>
      </div>

      {/* Tagline */}
      <div className={`tagline ${isLoaded ? 'animate-in' : ''}`}>
        <span>เร็วกว่า</span> <span className="highlight">แรงกว่า</span> <span>สะดวกกว่า</span>
      </div>
    </section>
  );
};

export default Hero;
